mapper.select:
select *
from shop_addr
where addr_idx= #{ addr_idx}

mapper.update:
update shop_addr 
	set addr_name = #{addr_name}
	, addr_phone = #{addr_phone}
	, addr_addr = #{addr_addr}
	where addr_idx = #{addr_idx}

update shop_user
set user_phone = #{user_phone}, user_email = #{user_email}, user_addr = #{user_addr}
where user_id = #{user_id}
